fn workspace_dir() -> std::path::PathBuf {
    let output = std::process::Command::new(env!("CARGO"))
        .arg("locate-project")
        .arg("--workspace")
        .arg("--message-format=plain")
        .output()
        .unwrap()
        .stdout;
    let cargo_path = std::path::Path::new(std::str::from_utf8(&output).unwrap().trim());
    cargo_path.parent().unwrap().to_path_buf()
}

/// Creates local Anvil instance.
///
/// Used for testing. When block time is given, new blocks are mined periodically.
/// Otherwise, a new block is mined per transaction.
pub fn create_anvil(block_time: Option<std::time::Duration>) -> ethers::utils::AnvilInstance {
    let mut anvil = ethers::utils::Anvil::new().path(workspace_dir().join(".foundry/bin/anvil"));

    if let Some(bt) = block_time {
        anvil = anvil.block_time(bt.as_secs());
    }

    anvil.spawn()
}

/// Mints specified amount of HOPR tokens to the contract deployer wallet.
/// Assumes that the `hopr_token` contract is associated with a RPC client that also deployed the contract.
/// Returns the block number at which the minting transaction was confirmed.
pub async fn mint_tokens<M: Middleware + 'static>(hopr_token: HoprToken<M>, amount: U256) -> u64 {
    let deployer = hopr_token.client().default_sender().expect("client must have a signer");
    hopr_token
        .grant_role(hopr_token.minter_role().await.unwrap(), deployer.into())
        .send()
        .await
        .unwrap()
        .await
        .unwrap();

    hopr_token
        .mint(
            deployer.into(),
            amount.into(),
            ethers::types::Bytes::new(),
            ethers::types::Bytes::new(),
        )
        .send()
        .await
        .unwrap()
        .await
        .unwrap()
        .unwrap()
        .block_number
        .unwrap()
        .as_u64()
}

/// Creates a transaction that transfers the given `amount` of native tokens to the
/// given destination.
pub fn create_native_transfer(to: Address, amount: U256) -> TypedTransaction {
    let mut tx = TypedTransaction::Eip1559(Eip1559TransactionRequest::new());
    tx.set_to(H160::from(to));
    tx.set_value(amount);
    tx
}

/// Funds the given wallet address with specified amount of native tokens and HOPR tokens.
/// These must be present in the client's wallet.
pub async fn fund_node<M: Middleware>(
    node: Address,
    native_token: U256,
    hopr_token: U256,
    hopr_token_contract: HoprToken<M>,
) -> () {
    let native_transfer_tx = Eip1559TransactionRequest::new()
        .to(NameOrAddress::Address(node.into()))
        .value(native_token);

    let client = hopr_token_contract.client();

    client
        .send_transaction(native_transfer_tx, None)
        .await
        .unwrap()
        .await
        .unwrap();

    hopr_token_contract
        .transfer(node.into(), hopr_token.into())
        .send()
        .await
        .unwrap()
        .await
        .unwrap();
}

/// Funds the channel to the counterparty with the given amount of HOPR tokens.
/// The amount must be present in the wallet of the client.
pub async fn fund_channel<M: Middleware>(
    counterparty: Address,
    hopr_token: HoprToken<M>,
    hopr_channels: HoprChannels<M>,
    amount: U256,
) {
    hopr_token
        .approve(hopr_channels.address(), amount.into())
        .send()
        .await
        .unwrap()
        .await
        .unwrap();

    hopr_channels
        .fund_channel(counterparty.into(), amount.as_u128())
        .send()
        .await
        .unwrap()
        .await
        .unwrap();
}

/// Deploy a safe instance and a module instance
/// Notice that to complete the on-boarding process,
/// 1) node should be included to the module
/// 2) announcement contract should be a target in the module
/// Notice that to be able to open channels, the deployed safe should have HOPR tokens and approve token transfer for Channels contract on the token contract
/// Returns (module address, safe address)
pub async fn deploy_one_safe_one_module_and_setup_for_testing<M: Middleware>(
    instances: &ContractInstances<M>,
    provider: Arc<M>,
    deployer: &ChainKeypair,
) -> Result<(Address, Address), ContractError<M>> {
    // Deploy Safe diamond deployment proxy singleton
    let safe_diamond_proxy_address = {
        // Fund Safe singleton deployer 0.01 anvil-eth and deploy Safe singleton
        let mut tx = Eip1559TransactionRequest::new();
        tx = tx.to(H160::from_str("E1CB04A0fA36DdD16a06ea828007E35e1a3cBC37").unwrap());
        tx = tx.value(10000000000000000u128);

        provider
            .send_transaction(tx, None)
            .await
            .map_err(|e| ContractError::MiddlewareError { e })?
            .await?;

        let tx = provider.send_raw_transaction(
            hex!("")
                .into()).await.map_err(|e| ContractError::MiddlewareError {e})?.await?.unwrap();
        tx.contract_address.unwrap()
    };
    debug!("Safe diamond proxy singleton {:?}", safe_diamond_proxy_address);

    // Get deployer address
    let self_address: types::Address = deployer.public().to_address().into();

    // Deploy minimum Safe suite
    {
        // 1. Safe proxy factory deploySafeProxyFactory();
        let _tx_safe_proxy_factory = TransactionRequest::new().to(safe_diamond_proxy_address).data::<Bytes>(
            hex!("").into()
        );
        // 2. Handler: only CompatibilityFallbackHandler and omit TokenCallbackHandler as it's not used now
        let _tx_safe_compatibility_fallback_handler = TransactionRequest::new().to(safe_diamond_proxy_address).data::<Bytes>(
            hex!("").into()
        );
        // 3. Library: only MultiSendCallOnly and omit MultiSendCall
        let _tx_safe_multisend_call_only = TransactionRequest::new().to(safe_diamond_proxy_address).data::<Bytes>(
            hex!("").into()
        );
        // 4. Safe singleton deploySafe();
        let _tx_safe_singleton = TransactionRequest::new().to(safe_diamond_proxy_address).data::<Bytes>(
            hex!("").into()
        );
        // other omitted libs: SimulateTxAccessor, CreateCall, and SignMessageLib
        // broadcast those transactions
        provider.send_transaction(_tx_safe_proxy_factory, None).await.unwrap().await.unwrap();
        provider.send_transaction(_tx_safe_compatibility_fallback_handler, None).await.unwrap().await.unwrap();
        provider.send_transaction(_tx_safe_multisend_call_only, None).await.unwrap().await.unwrap();
        provider.send_transaction(_tx_safe_singleton, None).await.unwrap().await.unwrap();
    }

    // create a salt from the nonce
    let curr_nonce = provider
        .get_transaction_count(self_address, Some(BlockNumber::Pending.into()))
        .await
        .unwrap();
    debug!("curr_nonce {:?}", curr_nonce.to_string());

    let nonce = keccak256(encode_packed(&[Token::Address(self_address), Token::Uint(curr_nonce)]).unwrap());
    let default_target = format!("{:?}010103020202020202020202", instances.channels.address());

    let contract_addrs = ContractAddresses::from(instances);

    debug!("contracts {:?}", contract_addrs);
    debug!("self_address {:?}", self_address);
    debug!("nonce {:?}", U256::from(nonce).to_string());
    debug!("default_target in bytes {:?}", Bytes::from_str(&default_target));
    debug!("default_target in u256 {:?}", U256::from_str(&default_target).unwrap());

    let typed_tx = instances
        .stake_factory
        .clone(
            instances.module_implementation.address(),
            vec![self_address],
            nonce.into(),
            U256::from_str(&default_target).unwrap().into(),
        )
        .tx;
    debug!("typed_tx {:?}", typed_tx);

    // deploy one safe and one module
    let instance_deployment_tx_logs = instances
        .stake_factory
        .clone(
            instances.module_implementation.address(),
            vec![self_address],
            nonce.into(),
            U256::from_str(&default_target).unwrap().into(),
        )
        .send()
        .await?
        .await?
        .unwrap()
        .logs;

    // decode the log.
    let module_log = instance_deployment_tx_logs
        .iter()
        .find(|log| {
            log.topics[0].eq(&"0x813d391dc490d6c1dae7d3fdd555f337533d1da2c908c6efd36d4cf557a63206".parse::<H256>().unwrap())
        })
        .ok_or(ContractError::ContractNotDeployed)?; // "NewHoprNodeStakeModule(address,address)"

    let safe_log = instance_deployment_tx_logs
        .iter()
        .find(|log| {
            log.topics[0].eq(&"0x8231d169f416b666ae7fa43faa24a18899738075a53f32c97617d173b189e386".parse::<H256>().unwrap())
        })
        .ok_or(ContractError::ContractNotDeployed)?; // "NewHoprNodeStakeSafe(address)"

    // get address of deployed instances
    let deployed_module_address: Address = instances
        .stake_factory
        .decode_event::<NewHoprNodeStakeModuleFilter>(
            "NewHoprNodeStakeModule",
            module_log.topics.clone(),
            module_log.data.clone(),
        )
        .unwrap()
        .module_implementation.into();

    // note that using the following snippet would cause error 
    // "DetokenizationError(InvalidOutputType("Expected Tuple, got Address(0x8819c5bab7d63c61d72f65b19b05a6772f55391b)"))"
    // ```
    // let decoded_safe_log: NewHoprNodeStakeSafeFilter = instances
    // .stake_factory
    // .decode_event("NewHoprNodeStakeSafe", safe_log.topics.clone(), safe_log.data.clone())
    // .unwrap();
    // ```
    let deployed_safe_address: Address = instances.stake_factory.abi()
        .event("NewHoprNodeStakeSafe")
        .unwrap()
        .parse_log(RawLog { topics:safe_log.topics.clone(), data: safe_log.data.clone().to_vec() })?
        .params.into_iter()
        .map(|param| param.value).collect::<Vec<_>>().pop().unwrap()
        .into_address().unwrap().into();

    debug!("instance_deployment_tx module instance {:?}", deployed_module_address);
    debug!("instance_deployment_tx safe instance {:?}", deployed_safe_address);

    Ok((deployed_module_address, deployed_safe_address))
}
