#![feature(test)]

use std::fmt;
use std::io::Write;
use test::{black_box, Bencher};

extern crate test;

struct HexBufferFormat<const N: usize>([u8; N]);
impl<const N: usize> fmt::Display for HexBufferFormat<N> {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        let mut buffer = const_hex::Buffer::<N>::new();
        f.write_str(buffer.format(&self.0))
    }
}

struct StdFormat<const N: usize>([u8; N]);
impl<const N: usize> fmt::Display for StdFormat<N> {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        for byte in &self.0 {
            write!(f, "{byte:02x}")?;
        }
        Ok(())
    }
}

macro_rules! benches {
    ($($name:ident($value:expr))*) => {
        mod encode_const_hex {
            use super::*;

            $(
                #[bench]
                fn $name(b: &mut Bencher) {
                    let buf = &mut [0; $value.len() * 2];

                    b.iter(|| {
                        let res = const_hex::encode_to_slice(black_box($value), black_box(buf));
                        black_box(res.unwrap());
                    });
                }
            )*
        }

        mod encode_hex {
            use super::*;

            $(
                #[bench]
                fn $name(b: &mut Bencher) {
                    let buf = &mut [0; $value.len() * 2];

                    b.iter(|| {
                        let res = hex::encode_to_slice(black_box($value), black_box(buf));
                        black_box(res.unwrap());
                    });
                }
            )*
        }

        mod format_const_hex {
            use super::*;

            $(
                #[bench]
                fn $name(b: &mut Bencher) {
                    let mut buf = Vec::with_capacity($value.len() * 2);

                    b.iter(|| {
                        buf.clear();
                        write!(&mut buf, "{}", HexBufferFormat(black_box($value))).unwrap();
                        black_box(&buf);
                    });
                }
            )*
        }

        mod format_std {
            use super::*;

            $(
                #[bench]
                fn $name(b: &mut Bencher) {
                    let mut buf = Vec::with_capacity($value.len() * 2);

                    b.iter(|| {
                        buf.clear();
                        write!(&mut buf, "{}", StdFormat(black_box($value))).unwrap();
                        black_box(&buf);
                    });
                }
            )*
        }
    }
}

